sys_ui_action.script:
/*An Ajax call to script include*/
function clickAction() {
    //alert('Test UI Action');
    var gaAS = new GlideAjax('x_757927_automat_0.AssetStockroom');
    gaAS.addParam('sysparm_name', 'processAssetTags');
    gaAS.addParam('sysparm_stockroom', g_form.getValue('stockroom'));
    gaAS.addParam('sysparm_assetTags', g_form.getValue('asset_tags'));
    gaAS.getXMLAnswer(callBackFunction);
}

function callBackFunction(response) {
    //var msg = response.responseXML.documentElement.getAttribute('answer');
    if (response != null && response != '') {
        //alert(response);
        if (response != 'success') {
            var answer = confirm('There are some Invalid Asset Tags, would you like to remove these : ' + response);
            if (answer == true) {
                //Remove the Invalid Asset tags
                clearAssetTags(response);
            }
        }
    }
}

function clearAssetTags(tagsToRemove) {
    //alert(tagsToRemove);
    var assetTags = g_form.getValue("asset_tags");
    var tagsArray = assetTags.split(',');

    // Remove the specified tags from the array
    tagsArray = tagsArray.filter(function(tag) {
        return !tagsToRemove.includes(tag.trim());
    });

    // Join the filtered array back into a comma-separated string
    var updatedAssetTags = tagsArray.join(',');

    // Set the updated value back to the field
    g_form.setValue("asset_tags", updatedAssetTags);
}
